# Amounts -> Amount in GWEI, we will be using 0.0001 of the SELL token to buy at least 10 of the BUY token
amount_in = int(0.0001 * 10**18)
min_amount_out = int(10 * 10**18)


# W3 and Account
w3 = Web3(Web3.HTTPProvider(rpc_endpoint))
account = Account.from_key(private_key)

# Router Codec
codec = RouterCodec()


# Functions
def get_contracts(address, abi):
    return w3.eth.contract(address=address, abi=abi)


def get_balance(contract, address):
    return contract.functions.balanceOf(address).call()

def approve_token(Permit2_address, Permit2_abi, Sell_balance, Sell_contract, Sell_address, Ur_address, Chain_id):
    permit2_address = Web3.to_checksum_address(Permit2_address)
    permit2_allowance = Sell_balance
    contract_function = Sell_contract.functions.approve(
            permit2_address,
            permit2_allowance
    )

    print ("Gas Prices: ", w3.eth.gas_price)
    trx_params = contract_function.build_transaction(
            {
                "from": account.address,
                "gas": 0,
                "maxPriorityFeePerGas": w3.eth.max_priority_fee,
                "maxFeePerGas": w3.eth.gas_price,
                "type": '0x2',
                "chainId": Chain_id,
                "value": 0,
                "nonce": w3.eth.get_transaction_count(account.address),
            }
        )
    gas = w3.eth.estimate_gas(trx_params)
    trx_params.update({'gas': gas})
    print("Gas used: ", gas)
    raw_transaction = w3.eth.account.sign_transaction(trx_params, account.key).rawTransaction
    trx_hash = w3.eth.send_raw_transaction(raw_transaction)
    print(f"Permit2 Sell Token approve trx hash: {trx_hash.hex()}")
    receipt = w3.eth.wait_for_transaction_receipt(trx_hash)
    print(f"Transaction receipt: {receipt}")

    print(
            "Permit2 Sell Token allowance:",
            Sell_contract.functions.allowance(account.address, permit2_address).call(),
    )

    permit2_contract = w3.eth.contract(address=permit2_address, abi=Permit2_abi)
    p2_amount, p2_expiration, p2_nonce = permit2_contract.functions.allowance(
            account.address,
            Sell_address,
            Ur_address,
    ).call()
    print("Allowance Granted",
            "p2_amount, p2_expiration, p2_nonce: ",
            p2_amount,
            p2_expiration,
            p2_nonce,
    )
    
    # permit message
    allowance_amount = permit2_allowance  # max/infinite
    permit_data, signable_message = codec.create_permit2_signable_message(
            Sell_address,
            allowance_amount,
            codec.get_default_expiration(),  # 30 days
            p2_nonce,
            ur_address,
            codec.get_default_deadline(),  # 180 seconds
            chain_id,
        )

    signed_message = account.sign_message(signable_message)
    return signed_message



def buy_token(sell_address, buy_address, amount_in, min_amount_out, ur_address, chain_id, use_token=False):

    encoded_input = (
            codec
            .encode
            .chain()
            .wrap_eth(FunctionRecipient.ROUTER, amount_in)
            .v2_swap_exact_in(FunctionRecipient.SENDER, amount_in, min_amount_out, [sell_address, buy_address], payer_is_sender=use_token)
            .build(codec.get_default_deadline())
    )


    trx_params = {
            "from": account.address,
            "to": ur_address,
            "gas": 300_000,
            "maxPriorityFeePerGas": w3.eth.max_priority_fee,
            "maxFeePerGas": w3.eth.gas_price,
            "type": '0x2',
            "chainId": chain_id,
            "value": amount_in,
            "nonce": w3.eth.get_transaction_count(account.address),
            "data": encoded_input,
    }
    gas = w3.eth.estimate_gas(trx_params)
    trx_params.update({'gas': gas})
    print("Gas used: ", gas)




